import React, {Component} from "react";
import DISHES from "../../data/dishes";
import MenuItem from "./MenuItem";

class Menu extends Component{
    state={
        dishes: DISHES,
        selectedDish: null
    }
   onDishSelect=(dish)=>{
        this.setState({
            selectedDish: dish
        })
   }

    render() {
        let menu = this.state.dishes.map(item=>{
            return(
                <MenuItem
                   key={item.id}
                   dish={item}
                   dishSelect={()=>this.onDishSelect(item)}
                />
            )
        })
        return (
         <div className='row'>
              <div className="col-6">
                  {menu}
              </div>
         </div>
        );
    }
}
export default Menu
