import React from "react";
import {Card, CardImg, CardBody, CardImgOverlay} from 'reactstrap';

const MenuItem=(props)=>{
    const {name,image,dishSelect}= props.dish
    return(
       <Card className='mt-4'>
           <CardBody onClick={dishSelect}>
               <CardImg style={{opacity: "0.5"}} src={image} width='100%'/>
               <CardImgOverlay>
                   {name}
               </CardImgOverlay>
           </CardBody>
       </Card>
    )
}
export default MenuItem
