import DISHES from "../data/dishes";
import COMMENTS from "../data/comments";
import {combineReducers} from "redux";
import {ADD_COMMENT} from "./ActionTypes";

const dishesReducer = (stateDishes=DISHES,action) =>{
    return stateDishes
}
const commentsReducer = (stateComments=COMMENTS,action)=>{
    if(action.type===ADD_COMMENT){
        const newComment = action.payload
        newComment.date = new Date().toDateString()
        newComment.id = stateComments.length
        return stateComments.concat(newComment)
    }
    return stateComments
}

export const Reducer = combineReducers({
    dishes: dishesReducer,
    comments: commentsReducer
})
