import DISHES from "../data/dishes";
import COMMENTS from "../data/comments";

const initialState = {
    dishes: DISHES,
    comments: COMMENTS,

}

export const Reducer = (state=initialState,action)=>{
     if(action.type==="ADD_COMMENT"){

         return {
             ...state,
             comments: state.comments.concat(action.payload)
         }
     }
    return state
}
