import DISHES from "../data/dishes";
import COMMENTS from "../data/comments";

const initialState = {
    dishes: DISHES,
    comments: COMMENTS,
    sample: "Khorshed Alam"
}

export const Reducer = (state=initialState,action)=>{
    if(action.type==="SAMPLE"){
        return {
            ...state,
            sample: action.sample
        }
    }
    return state
}
